import { OneOfImpl } from './OneOf';
export function If({ is, then, otherwise }) {
    return new OneOfImpl(is.map(v => v ? [1, true] : [2, false]), {
        1: () => then,
        2: () => otherwise
    });
}
export function When({ is, children }) {
    return new OneOfImpl(is.map(v => v ? [1, true] : [2, false]), {
        1: () => children,
        2: () => null
    });
}
export function Unless({ is, children }) {
    return new OneOfImpl(is.map(v => v ? [1, true] : [2, false]), {
        1: () => null,
        2: () => children
    });
}
